<script>
    photoAdmin = (function()
    {
        return {
            init: function()
            {
                $('.ow_photo_set_c_grid,.ow_photo_set_p_grid,.ow_photo_set_c_album,.ow_photo_set_p_album,.ow_photo_set_c_view,.ow_photo_set_p_view').on('click', function( event )
                {
                    var self = $(this);
                    var name = self.attr('name');

                    if ( self.find('.ow_checkbox_cell').hasClass('ow_checkbox_cell_marked') )
                    {
                        self.find('.ow_checkbox_cell').removeClass('ow_checkbox_cell_marked');
                        $(name).addClass('ow_checkbox_cell_marked');
                    }
                    else
                    {
                        $(name).removeClass('ow_checkbox_cell_marked');
                        self.find('.ow_checkbox_cell').addClass('ow_checkbox_cell_marked');
                    }
                });

                var form = window.owForms['view-mode'];
                form.bind('submit', function( data )
                {
                    data['photo-list-mode'] = $('.photo_list_mode_c').hasClass('ow_checkbox_cell_marked') ? 1 : 0;
                    data['album-list-mode'] = $('.album_list_mode_c').hasClass('ow_checkbox_cell_marked') ? 1 : 0;
                    data['photo-view-mode'] = $('.photo_view_mode_c').hasClass('ow_checkbox_cell_marked') ? 1 : 0;
                });

                var elements = form.elements;
                elements['photo-list-mode'].input.value.trim() !== '0' ? $('.photo_list_mode_c').addClass('ow_checkbox_cell_marked') : $('.photo_list_mode_p').addClass('ow_checkbox_cell_marked');
                elements['album-list-mode'].input.value.trim() !== '0' ? $('.album_list_mode_c').addClass('ow_checkbox_cell_marked') : $('.album_list_mode_p').addClass('ow_checkbox_cell_marked');
                elements['photo-view-mode'].input.value.trim() !== '0' ? $('.photo_view_mode_c').addClass('ow_checkbox_cell_marked') : $('.photo_view_mode_p').addClass('ow_checkbox_cell_marked');
            }
        };
    })(jQuery);
</script>
